// ignore:start
"use strict";

/* globals*/
/* exported*/

// ignore:end

class PackageVersion {
    constructor (version_str) {
        const v = version_str.split('.');
        this.major  = Number(v[0]);
        this.middle = Number(v[1]);
        this.minor  = Number(v[2]);
    }

    toString () {
        const { major, middle, minor } = this;
        return `${ major }.${ middle }.${ minor }`;
    }
}

module.exports = PackageVersion;
