
PUBLISH_TEMP_DIR=$1

if [ -f package-lock.json ]; then
    cp package-lock.json "$PUBLISH_TEMP_DIR"
fi

if [ -d bin ]; then
    cp -R bin "$PUBLISH_TEMP_DIR"
fi

cp -R src/* LICENSE README.md "$PUBLISH_TEMP_DIR" && \
    cd "$PUBLISH_TEMP_DIR" && npm publish --access public && \
    cd .. && sleep 1
